data.value:
-- database unit test for agg.IncludeDate
declare @dateTimeId int,
	@adBeginDate date,
	@adEndDate date,
	@beginDateTimeId int,
	@endDateTimeId int,
	@timeInterval int;
	
-- null ad begin/dates
select
    @dateTimeId = 0,
    @adBeginDate = NULL,
    @adEndDate = NULL,
    @beginDateTimeId = 0,
    @endDateTimeId = 0,
    @timeInterval = 0;
select [agg].[IncludeDate](@dateTimeId, @adBeginDate, @adEndDate, @beginDateTimeId, @endDateTimeId, @timeInterval);

-- hourly date time id, hourly interval
select
    @dateTimeId = 2015030501,
    @adBeginDate = '03/05/2015',
    @adEndDate = '03/05/2015',
    @beginDateTimeId = 2015030500,
    @endDateTimeId = 2015030500,
    @timeInterval = 0;
select [agg].[IncludeDate](@dateTimeId, @adBeginDate, @adEndDate, @beginDateTimeId, @endDateTimeId, @timeInterval);

-- daily date time id, daily interval
select
    @dateTimeId = 2015030500,
    @adBeginDate = '03/05/2015',
    @adEndDate = '03/05/2015',
    @beginDateTimeId = 2015030500,
    @endDateTimeId = 2015030500,
    @timeInterval = 2;
select [agg].[IncludeDate](@dateTimeId, @adBeginDate, @adEndDate, @beginDateTimeId, @endDateTimeId, @timeInterval);

-- monthly date time id, monthly interval
select
    @dateTimeId = 2015030000,
    @adBeginDate = '03/05/2015',
    @adEndDate = '03/05/2015',
    @beginDateTimeId = 2015030500,
    @endDateTimeId = 2015030500,
    @timeInterval = 4;
select [agg].[IncludeDate](@dateTimeId, @adBeginDate, @adEndDate, @beginDateTimeId, @endDateTimeId, @timeInterval);

-- weekly date time id, weekly interval
select
    @dateTimeId = 201510,
    @adBeginDate = '03/05/2015',
    @adEndDate = '03/05/2015',
    @beginDateTimeId = 201510,
    @endDateTimeId = 201510,
    @timeInterval = -2;
select [agg].[IncludeDate](@dateTimeId, @adBeginDate, @adEndDate, @beginDateTimeId, @endDateTimeId, @timeInterval);

